close all;clear all;clc;# https://tomeko.net/online_tools/hex_to_file.php?lang=enfid = fopen('myfile.dat','r');val = fread(fid);data0 = val(17:length(val));data = 1:1:(length(data0)/2);for i = 1:1:length(data)  data(i) = data0(2*i-1) + data0(2*i)*256;      if(data(i)==(256*256-1))     data(i) = 0;  endif  endforV = 1:1:(length(data)/5);ref = 1:1:(length(data)/5);C = 1:1:(length(data)/5);Z = 1:1:(length(data)/5);G = 1:1:(length(data)/5);for i = 1:1:(length(data)/5)   V(i) = data(i*5-4);   ref(i) = data(i*5-3);   C(i) = data(i*5-2);   Z(i) = data(i*5-1);   G(i) = data(i*5);endforfigure(1);plot(V,'.');fclose(fid);